update `reports_query_details` set `query_detail`='SELECT UUID() as uuid,po.id AS po_id ,po.client_order_id ,DATE(po.order_date) AS order_date ,(SELECT status FROM sbs_platform_order.order_status WHERE id = po.order_status_id) AS order_status ,(SELECT status FROM sbs_platform_order.line_item_status WHERE id = li.line_item_status_id) AS line_item_status ,(SELECT DATE(modified_date) FROM sbs_platform_order.order_status WHERE order_id = po.id AND status LIKE ''%SHIPPED%'' GROUP BY order_id) AS complete_ship_date ,(SELECT TRIM(message) FROM sbs_platform_order.tracking_status WHERE LEFT(TRIM(message),2) = ''1Z'' AND destination_id = d.id GROUP BY destination_id) AS tracking_number ,(SELECT value FROM sbs_platform_order.order_property WHERE order_id = po.id AND name = ''p3Segment'') AS p3Segment ,li.id AS li_id ,li.customer_product_id ,CAST((CASE WHEN (SELECT value FROM sbs_platform_order.destination_property WHERE destination_id = d.id AND name = ''quantityOrdered'') IS NULL THEN lic.quantity ELSE (SELECT value FROM sbs_platform_order.destination_property WHERE destination_id = d.id AND name = ''quantityOrdered'') END) AS UNSIGNED) AS quantity ,lic.id AS lic_id ,lic.customer_sku ,(CASE WHEN LOCATE(''.'',lic.customer_sku) > 0 THEN LEFT(lic.customer_sku,LOCATE(''.'',lic.customer_sku)-1) ELSE lic.customer_sku END) AS sku_trimmed ,(SELECT value FROM sbs_platform_order.line_item_component_custom_property WHERE line_item_component_id = lic.id AND NAME = ''productName'') AS product_name ,(CASE WHEN LOWER(li.customer_product_id) LIKE ''%kit%'' THEN 1 END) as kit_identifier FROM sbs_platform_order.platform_order po JOIN sbs_platform_order.line_item li ON po.id = li.order_id JOIN sbs_platform_order.line_item_component lic ON li.id = lic.line_item_id JOIN sbs_platform_order.destination d ON li.id = d.line_item_id WHERE po.program_id = 3 AND ( (SELECT DATE(modified_date) FROM sbs_platform_order.line_item_status WHERE line_item_id = li.id AND status = ''SHIPPED'' GROUP BY line_item_id) IS NOT NULL OR ((SELECT status FROM sbs_platform_order.line_item_status WHERE id = li.line_item_status_id) != ''SHIPPED'' AND LEFT((SELECT TRIM(message) FROM sbs_platform_order.tracking_status WHERE LEFT(TRIM(message),2) = ''1Z'' AND destination_id = d.id GROUP BY destination_id),2) = ''1Z'') ) AND (SELECT value FROM sbs_platform_order.line_item_component_custom_property WHERE line_item_component_id = lic.id AND NAME = ''productName'') LIKE ''%All%%Savers%''' , `datasource` = '${schema}.', `created_date` = NOW(), `modified_date` = NOW()
where report_id = (SELECT report_id from reports_details where report_name = 'ALL SAVERS REPORTS')
