INSERT INTO `reports_details` ( `report_name`, `created_by`, `created_date`, `modified_by`, `modified_date`) VALUES ('MPT__REPORT_ORDER_COUNTS_BY_USERS', 'admin', NOW(), 'admin',NOW());

INSERT INTO `reports_query_details` (`report_id`) SELECT report_id from reports_details where report_name = 'MPT__REPORT_ORDER_COUNTS_BY_USERS';

update `reports_query_details` set `query_detail`='SELECT (SELECT TRIM(value) FROM ${schema}.order_property WHERE order_id = po.id AND name = ''userName'') AS userName ,TRIM(pv.print_vendor) AS print_vendor ,COUNT(DISTINCT po.id) AS ordercount ,COUNT(DISTINCT (CASE WHEN LOWER(li.customer_product_id) LIKE ''%kit%'' THEN li.id END)) AS kitcount ,SUM(li.quantity) AS quantity FROM ${schema}.platform_order po JOIN ${schema}.line_item li ON po.id = li.order_id LEFT JOIN ${schema}.print_vendor pv ON pv.id = li.print_vendor WHERE po.program_id = 3 GROUP BY (SELECT TRIM(value) FROM ${schema}.order_property WHERE order_id = po.id AND name = ''userName'') ,TRIM(pv.print_vendor)' , `datasource` = '${schema}', `created_date` = NOW(), `modified_date` = NOW()
where report_id = (SELECT report_id from reports_details where report_name = 'MPT__REPORT_ORDER_COUNTS_BY_USERS')
